import React from "react";
import Carousel from "react-bootstrap/Carousel";
import "bootstrap/dist/css/bootstrap.min.css";
import shopping1 from "./Images/Banners/shopping1.jpg";
import shopping2 from "./Images/Banners/shopping2.jpg";
import shopping3 from "./Images/Banners/shopping3.jpg";
import shopping4 from "./Images/Banners/shopping4.jpg";
const Banner = () => {
  return (
    <Carousel>
      <Carousel.Item>
        <img className="d-block w-100" src={shopping1} alt="First slide" />
        <Carousel.Caption>
          <h3>Shop More in Big Million Days</h3>
          <p>Buy Women's Wear.</p>
          <p>flat 50% off</p>
        </Carousel.Caption>
      </Carousel.Item>
      {/* <Carousel.Item>
        <img className="d-block w-100" src={shopping4} alt="Second slide" />

        <Carousel.Caption>
          <h3>Second slide label</h3>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        </Carousel.Caption>
      </Carousel.Item> */}
      <Carousel.Item>
        <img className="d-block w-100" src={shopping3} alt="Third slide" />

        <Carousel.Caption>
          <h3>Third slide label</h3>
          <p>
            Praesent commodo cursus magna, vel scelerisque nisl consectetur.
          </p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img className="d-block w-100" src={shopping2} alt="First slide" />
        <Carousel.Caption>
          <h3>First slide label</h3>
          <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
        </Carousel.Caption>
      </Carousel.Item>
    </Carousel>
  );
};

export default Banner;
