import React, { useState } from "react";
import Navbar from "../Navbar/Navbar";
import { Link, useNavigate } from "react-router-dom";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "../Config/FirebaseConfig";
import "./Signup.css";
import { Form } from "formik";
import { useFormik } from "formik";
import { signUpSchema } from "./Schema";
// import { useForm } from "react-hook-form";
const initialValues = {
  name: "",
  number: "",
  email: "",
  password: "",
};
const Signup = () => {
  const [userName, setUserName] = useState("");
  const [mobileNumber, setMobileNumber] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [successMsg, setSuccessMsg] = useState("");

  const { values, errors, touched, handleBlur, handleChange, handleSubmit } =
    useFormik({
      initialValues: initialValues,
      validationSchema: signUpSchema,
      onSubmit: (values, action) => {
        console.log(values);
        action.resetForm();
      },
    });
  console.log(errors);

  const navigate = useNavigate();
  //
  const submitForm = async (event) => {
    event.preventDefault();

    createUserWithEmailAndPassword(auth, email, password)
      .then((auth) => {
        console.table(auth._tokenResponse.localId);
        navigate("./login");
        storeUserData(auth._tokenResponse.localId);
      })

      .catch((error) => {
        console.error(error);
      });

    async function storeUserData(id) {
      const initialcartvalue = 0;
      if (userName && email && mobileNumber) {
        const res = await fetch(
          "https://react-firebase-91120-default-rtdb.firebaseio.com/usersdata/" +
            id +
            ".json",
          {
            method: "PATCH",
            body: JSON.stringify({
              name: userName,
              number: mobileNumber,
              email: email,
              initialcartvalue: initialcartvalue,
            }),
          }
        );

        if (res) {
          setUserName("");
          setEmail("");
          setMobileNumber("");
          setPassword("");
          setSuccessMsg(
            "New User added Successfully , You will be redirected to Login page"
          );
          setTimeout(() => {
            setSuccessMsg("");
            navigate("/login");
          }, 2000);
        }
      } else {
        // setErrorMsg(error);
      }
    }
  };

  return (
    <div>
      <Navbar />
      <div className="signup-container">
        <form className="signup-form" method="PATCH" onSubmit={handleSubmit}>
          <p>Create Account</p>
          {successMsg && <div className="success-msg">{successMsg} </div>}

          {/* {errorMsg && (
            <>
              <div className="error-msg">{errorMsg}</div>
            </>
          )} */}

          <label>Your Name</label>
          <input
            // onChange={(e) => setUserName(e.target.value)}
            type="text"
            name="name"
            placeholder="Enter first and last name"
            value={values.name}
            onChange={handleChange}
            onBlur={handleBlur}
          />
          {errors.name && touched.name ? (
            <p style={{ fontSize: "19px" }} className="form-error">
              {errors.name}
            </p>
          ) : null}

          <label>Mobile Number</label>
          <input
            // onChange={(e) => setMobileNumber(e.target.value)}
            type="number"
            name="number"
            placeholder="Mobile number"
            value={values.number}
            onChange={handleChange}
            onBlur={handleBlur}
          />
          {errors.number && touched.number ? (
            <p style={{ fontSize: "19px" }} className="form-error">
              {errors.number}
            </p>
          ) : null}

          <label>Email</label>
          <input
            // onChange={(e) => setEmail(e.target.value)}
            type="email"
            name="email"
            placeholder="Enter  your Email"
            value={values.email}
            onChange={handleChange}
            onBlur={handleBlur}
          />
          {errors.email && touched.email ? (
            <p style={{ fontSize: "19px" }} className="form-error">
              {errors.email}
            </p>
          ) : null}

          <label>Password</label>
          <input
            // onChange={(e) => setPassword(e.target.value)}
            type="password"
            name="password"
            placeholder="Enter Your  Password"
            value={values.password}
            onChange={handleChange}
            onBlur={handleBlur}
          />
          {errors.password && touched.password ? (
            <p style={{ fontSize: "19px" }} className="form-error">
              {errors.password}
            </p>
          ) : null}

          <button onSubmit={submitForm} type="submit">
            Sign Up
          </button>
          <div>
            <span>Already have Account?</span>
            <Link to="/login">LogIn</Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Signup;
