import React, { useState } from "react";
import Navbar from "../Navbar/Navbar";
import { Link, useNavigate } from "react-router-dom";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "../Config/FirebaseConfig";
import "./Signup.css";

const Signup = () => {
  const [userName, setUserName] = useState("");
  const [mobileNumber, setMobileNumber] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMsg, setErrorMsg] = useState("");
  const [successMsg, setSuccessMsg] = useState("");

  const navigate = useNavigate();
  //
  const submitForm = async (event) => {
    event.preventDefault();

    createUserWithEmailAndPassword(auth, email, password)
      .then((auth) => {
        console.table(auth._tokenResponse.localId);
        navigate("./login");
        storeUserData(auth._tokenResponse.localId);
      })

      .catch((error) => {
        console.error(error);
      });

    async function storeUserData(id) {
      const initialcartvalue = 0;
      if (userName && email && mobileNumber) {
        const res = await fetch(
          "https://react-firebase-91120-default-rtdb.firebaseio.com/usersdata/" +
            id +
            ".json",
          {
            method: "PATCH",
            body: JSON.stringify({
              name: userName,
              number: mobileNumber,
              email: email,
              initialcartvalue: initialcartvalue,
            }),
          }
        );

        if (res) {
          setUserName("");
          setEmail("");
          setMobileNumber("");
          setPassword("");
          setSuccessMsg(
            "New User added Successfully , You will be redirected to Login page"
          );
          setTimeout(() => {
            setSuccessMsg("");
            navigate("/login");
          }, 2000);
        }
      } else {
        // setErrorMsg(error);
      }
    }
  };

  return (
    <div>
      <Navbar />
      <div className="signup-container">
        <form className="signup-form" method="PATCH" onSubmit={submitForm}>
          <p>Create Account</p>
          {successMsg && <div className="success-msg">{successMsg} </div>}

          {errorMsg && (
            <>
              <div className="error-msg">{errorMsg}</div>
            </>
          )}
          <label>Your Name</label>
          <input
            onChange={(e) => setUserName(e.target.value)}
            type="text"
            name="name"
            placeholder="Enter first and last name"
          />
          <label>Mobile Number</label>
          <input
            onChange={(e) => setMobileNumber(e.target.value)}
            type="number"
            name="name"
            placeholder="Mobile number"
          />
          <label>Email</label>
          <input
            onChange={(e) => setEmail(e.target.value)}
            type="email"
            name="name"
            placeholder="Enter  your Email"
          />
          <label>Password</label>
          <input
            onChange={(e) => setPassword(e.target.value)}
            type="password"
            name="name"
            placeholder="Enter Your  Password"
          />
          <button type="submit">Sign Up</button>
          <div>
            <span>Already have Account?</span>
            <Link to="/login">LogIn</Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Signup;
