import React from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Navbar.css";
import Cart from "../Component/Images/Cart.png";
import Profile from "../Component/Images/profile.png";
import Logo from "./Images/logo.png";
import { useAuthState } from "react-firebase-hooks/auth";
import { auth } from "../Component/Config/FirebaseConfig";
const Navbar = () => {
  const [user, loading, error] = useAuthState(auth);
  const navigate = useNavigate();
  const loggedUser = user?.email;
  const HandleLogout = () => {
    auth.signOut().then(() => {
      navigate("/login");
    });
  };
  return (
    <>
      <div className="navbar">
        <div className="LeftContainer">
          <img src={Logo} />
        </div>

        <div className="RightContainer">
          {!loggedUser && (
            <nav>
              <Link to="/">
                <button>Home</button>
              </Link>
              <Link to="/signup">
                <button>Signup</button>
              </Link>
              <Link to="/login">
                <button>Login</button>
              </Link>
              <div className="cart-btn">
                <img src={Cart} alt="no img " />
                <span className="cart-icon-css">0</span>
              </div>
              <Link to="/userprofile">
                <img src={Profile} alt=" no img" className="profile-icon" />
              </Link>
            </nav>
          )}
          {loggedUser && (
            <nav>
              <Link to="/">
                <button>Home</button>
              </Link>
              <Link to="/cart">
                <div className="cart-btn">
                  <img src={Cart} alt="no img " />
                  <span className="cart-icon-css">0</span>
                </div>
              </Link>
              <Link to="/userprofile">
                <img src={Profile} alt=" no img" className="profile-icon" />
              </Link>
              <button className="logout-btn" onClick={HandleLogout}>
                LogOut
              </button>
            </nav>
          )}
        </div>
      </div>

      {/* <nav>
        <Link to="/">
          <button>Home</button>
        </Link>
        <Link to="/signup">
          <button>Signup</button>
        </Link>
        <Link to="/login">
          <button>Login</button>
        </Link>
        <Link to="/cart">
          <div className="cart-btn">
            <img src={Cart} alt="no img " />
            <span className="cart-icon-css">0</span>
          </div>
        </Link>
        <Link to="/userprofile">
          <img src={Profile} alt=" no img" className="profile-icon" />
        </Link>
      </nav>{" "} */}
    </>
  );
};

export default Navbar;
