import React, { useState, useEffect } from "react";
import Navbar from "./Navbar";

import Banner from "./Banner";
import firebase from "firebase/compat/app";
import { auth } from "../Component/Config/FirebaseConfig";
import { useAuthState } from "react-firebase-hooks/auth";
import Footer from "./Footer";

const Home = () => {
  const [user] = useAuthState(auth);
  const loggedUser = user;

  return (
    <div>
      <Navbar />
      <Banner />
      {/* <Product /> */}
      <p>
        welcome
        <br />
        {loggedUser ? loggedUser.email : "No data"}
      </p>
      {/* <p> {loggedUser ? loggedUser.email : "No data"}</p> */}
      {/* // {console.log(user)} */}
      <div
        class="modal modal-tour position-static d-block bg-secondary py-5"
        tabindex="-1"
        role="dialog"
        id="modalTour"
      >
        <div class="modal-dialog" role="document">
          <div class="modal-content rounded-4 shadow">
            <div class="modal-body p-5">
              <h2 class="fw-bold mb-0">Hello {loggedUser.userName}</h2>

              <div>
                <h5 class="mb-0">Welcome In Let's Shop</h5>
                Share videos wherever you go.
              </div>

              <button
                type="button"
                class="btn btn-lg btn-primary mt-5 w-100"
                data-bs-dismiss="modal"
              >
                Great, thanks!
              </button>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Home;
