import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Navbar from "../Navbar/Navbar";
import "./Product.css";
import { Link } from "react-router-dom";

const Product = () => {
  const [productData, setProductData] = useState([]);
  const [filter, setFilter] = useState(productData);

  useEffect(() => {
    const getProducts = async () => {
      const res = await fetch("https://fakestoreapi.com/products");
      if (true) {
        setProductData(await res.clone().json());
        setFilter(await res.json());

        console.log(filter);
      }
    };
    getProducts();
  }, []);

  const filterProduct = (e) => {
    const updatedList = productData.filter((x) => x.category === e);
    setFilter(updatedList);
  };
  const ShowProducts = () => {
    return (
      <>
        <div className="row m-2 mb-4">
          <h3 style={{ color: "teal", fontFamily: "cursive" }}>
            Filter By Category
          </h3>
          <div className="col-3">
            <div className="button">
              <button
                className="btn btn-outline-warning mb-3"
                onClick={() => setFilter(productData)}
              >
                all
              </button>
              <br />
              <button
                className="btn btn-outline-warning mb-3"
                onClick={() => filterProduct("men's clothing")}
              >
                Men's Clothing
              </button>
              <br />
              <button
                className="btn btn-outline-warning mb-3"
                onClick={() => filterProduct("women's clothing")}
              >
                Women's Clothing
              </button>
              <br />
              <button
                className="btn btn-outline-warning mb-3"
                onClick={() => filterProduct("jewelery")}
              >
                Jewelery
              </button>
              <br />
              <button
                className="btn btn-outline-warning mb-3"
                onClick={() => filterProduct("electronics")}
              >
                Electronics
              </button>
              <br />
              <br />
            </div>
          </div>
          <div className="col-9">
            <div className="row ">
              {filter.map((product) => {
                return (
                  <>
                    <div
                      className="card h-70 text-center m-3 p-4"
                      key={product.id}
                      style={{ width: "18rem" }}
                    >
                      <img
                        src={product.image}
                        className="card-img-top"
                        alt={product.title}
                        height="250px"
                      />
                      <div className="card-body">
                        <h5 className="card-title mb-0">
                          {product.title.substring(0, 12)}
                        </h5>
                        <p className="card-text fw-bold">${product.price}</p>
                        <Link
                          to={`/productdetail/${product.id}`}
                          className="btn btn-outline-dark"
                        >
                          View Product
                        </Link>
                      </div>
                    </div>
                  </>
                );
              })}
            </div>
          </div>
        </div>
      </>
    );
  };
  return (
    <>
      <Navbar />
      <div className="my-5 py-5">
        <div className="row">
          <div className="col-12 mb-5">
            <h1 className="display-6 fw-bolder text-warning text-center">
              Latest Products
            </h1>
            <hr />
          </div>

          <div className="row-justify-content-center">
            <ShowProducts />
          </div>
        </div>
      </div>
    </>
  );
};

export default Product;
