import * as Yup from "yup";
export const checkOutSchema = Yup.object({
  firstname: Yup.string().min(4, "Please enter a name more than 4 character").max(20).required("Please enter you first name"),
  lastname: Yup.string().min(4, "Please enter a name more than 4 character").max(20).required("Please enter you  last name"),
  username: Yup.string().min(4, "Please enter a name more than 4 character").max(20).required("Please enter you user name"),
  address: Yup.string().min(5, "Please address more than 5").max(50).required("Please fill your address"),
  email: Yup.string().email().required("Please enter your email"),
  country: Yup.string().nullable(true).when(["address", "zipcode", "city"], {
    is: (address, zipcode, city) => address !== undefined || zipcode !== null || city !== undefined,
    then: Yup.string().required("requis"),
    otherwhise: Yup.string().nullable(true)
  }),
  zipcode: Yup.string().when(["address", "country", "city"], {
    is: (address, country, city) => address !== undefined || country !== undefined || city !== undefined,
    then: Yup.string().required("requis")
  })
});
