export const addItem = (product) => {
  return {
    type: "ADDITEM",
    payload: product,
  };
};
const delItem = (product) => {
  return {
    type: "DELITEM",
    payload: product,
  };
};
export const removeAll = (product) => {
  return {
    type: "REMOVEALL",
    payload: product,
  };
};
export { delItem };
